import React, { useState } from 'react';
// import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import './App.css';
// import Team from './pages/Team';

function Header() {
  return (
    <header className="header">
      <nav className="navbar">
        <a href="#about" className="nav-link">About</a>
        <a href="#model-details" className="nav-link">Model Details</a>
        <a href="#the-app" className="nav-link">The App</a>
        <Link to="/team" className="nav-link">The Team</Link>
      </nav>
    </header>
  );
}

function App() {
  const [selectedImage, setSelectedImage] = useState(null);

  const handleImageChange = (event) => {
    if (event.target.files && event.target.files[0]) {
      setSelectedImage(event.target.files[0]);
    }
  };

  const handleSave = () => {
    if (selectedImage) {
      // Save image logic here, like an API call to upload the file
      console.log("Image saved:", selectedImage);
      alert("Image saved successfully!");
    } else {
      alert("Please upload an image first.");
    }
  };

  return (
    <div>
      <Header />
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', padding: '50px' }}>
        <h1>Upload Image</h1>
        <input type="file" accept="image/*" onChange={handleImageChange} />
        {selectedImage && (
          <div style={{ margin: '20px' }}>
            <img
              src={URL.createObjectURL(selectedImage)}
              alt="Preview"
              style={{ maxWidth: '300px', maxHeight: '300px' }}
            />
          </div>
        )}
        <button onClick={handleSave} style={{ padding: '10px 20px', fontSize: '16px' }}>
          Save
        </button>
      </div>
    </div>
  );
}

export default App;
